import React, { useState, useEffect} from "react";


export default function AddToBooks() {

    const host="http://localhost:3000"
    const [title, setTitle]=useState(null);

    useEffect(() => {
        fetch(`${host}/bookslist`)
            .then(result => result.json())
            .then(title => setBooks(title))
            .catch((err) => console.warn(err))
    },[])

    const handleAdd=(newTitle) => {
        fetch(`${host}/bookslist`, {
            method:"POST",
            body:JSON.stringify(newTitle),
            headers:{
                "Content-Type":'application/json'
            }
        })
            .then((response) => response.json())
            .then((data => setTitle(prev=> [...prev, data])))
    }


    return(
        <>
            <AddToBooks  />
            <button><img src="../images/books.svg" alt="dodaj do listy książek"/></button>
        </>
    )
}
