import React, { useState, useEffect} from "react";
import {Link} from "react-router-dom";
import BooksList from "../lists/BooksList";
import EbooksList from "../lists/EbooksList";
import WishlistList from "../lists/WishlistList";
import BorrowedList from "../lists/BorrowedList";
import bookImg from "../images/books.svg";
import kindleImg from "../images/kindle.png";
import borrowedImg from "../images/books.svg";
import heartImg from "../images/heart.png";

export default function AddToBooks() {

    const host="http://localhost:3000"
    const [title, setTitle]=useState(null);
    const [author, setAuthor]=useState(null);
    const [note, setNote]=useState(null);
    const [paper, setPaper]=useState(true);
    const [ebook, setEbook]=useState(false);
    const [borrowed, setBorrowed]=useState(false);
    const [wishlist, setWishlist]=useState(true);

    useEffect(() => {
        fetch(`${host}/books`)
            .then(result => result.json())
            .then(title => setTitle(title))
            .then(author => setAuthor(author))
            .then(note => setNote(note))
            .then(paper => setPaper(true))
            .then (ebook => setEbook(false))
            .then(borrowed => setBorrowed(false))
            .then(wishlist => setWishlist(false))
            .catch((err) => console.warn(err))
    },[])

    const handleAdd=(newTitle) => {
        const err = [];
        fetch(`${host}/books`, {
            method:"POST",
            body:JSON.stringify(newTitle),
            headers:{
                "Content-Type":'application/json'
            }
        })
            .then((response) => response.json())
            .then((data => setTitle(prev=> [...prev, data])
                .then(author => setAuthor(author))
                .then(note => setNote(note))
                .then(paper => setPaper(true))
                .then (ebook => setEbook(false))
                .then(borrowed => setBorrowed(false))
                .then(wishlist => setWishlist(false))
                .catch((err) => console.warn(err))
            ))
        if (title.length < 2) {
            err.push("Tytuł jest za krótki");
        }
        if (author.length < 3) {
            err.push("Nazwisko jest za krótkie");
        }
        if (note.length > 100) {
            err.push("Notatka jest za długa")
    }

    return (
        <>
            <AddToBooks addTitle={handleAdd} />
            <ul>
                {title?.map((el)=>{
                    return<li key={el.title}>
                    <p>{el.title}</p>
                    <p>{el.author}</p>
                    <p>{el.note}</p>
                        <p>{el.paper} {el.ebook} {el.borrowed} {el.wishlist}</p>
                    <button className="button" onClick={handleAdd}><img src={bookImg} alt="dodaj do listy książek"/><BooksList/></button>
                    <button className="button"><img src={kindleImg} alt="dodaj do listy ebooków"/><EbooksList/></button>
                    <button className="button"><img src={borrowedImg} alt="dodaj do listy książek wypożyczonych"/><BorrowedList/></button>
                    <button className="button"><img src={heartImg} alt="dodaj do wishlist"/><WishlistList/></button>
                    </li>
                })};}
            </ul>
        </>
    )
}}
