import React, { useState } from "react";

export default function SortZA() {
    const [books, setBooks] = useState(books);

    const sortZA=()=> {
        const sortedAZ=books.sort((a, b)=>{
            return b.books-a.books;
        });
        setBooks(sortedAZ);
    }
    return(
        <div>
            {books.map((book, i)=> {
                return <li key={i}>{book}</li>
            })}
            <button onClick={sortZA}><img src="../images/a-z.svg" alt="sortuj z-a"/></button>
        </div>
    )
};
