import React, { useState } from "react";

export default function Add() {
    const [title, setTitle]=useState("");
    const [author, setAuthor]=useState("");
    const [note, setNote]=useState("");

    const handleAddBook=(e)=> {
        e.preventDefault();
        addBook({
            title:title,
            author:author,
            note:note,
        },
        )


        const err = [];

        if (title.length < 2) {
            err.push("Tytuł jest za krótki");
        }
        if (author.length < 3) {
            err.push("Nazwisko jest za krótkie");
        }
        if (note.length > 100) {
            err.push("Notatka jest za długa")
        }
    }
    return(
        <>
            <form className="form" onSubmit={handleAddBook}>
                <label htmlFor="title">tytuł</label>
                <input type="text"
                       className="title"
                       value={ title}
                       onChange={(e) => setTitle(e.target.value)}
                />
                <label htmlFor="author">autor</label>
                <input type="text"
                       className="author"
                       value={author}
                       onChange={setAuthor}/>
                <input type='text' className="note" value={note} onChange={setNote}/>
                <button className=" button plus"><img src="../images/plus.png" alt="add"/></button>
            </form>
        </>
    );
}
